mapper.sql:
SELECT sku.id AS sku_id, sku.sku_code, sku.sku_name, item.id AS item_id, item.item_name, item.item_code, w.id AS warehouse_id, w.warehouse_name, GROUP_CONCAT( CONCAT(inv.storage_shelf, ':', inv.quantity) ORDER BY inv.storage_shelf SEPARATOR ';' ) AS stock_info_text, SUM(inv.quantity) AS total_quantity FROM wms_inventory inv INNER JOIN wms_item_sku sku ON inv.sku_id = sku.id INNER JOIN wms_item item ON sku.item_id = item.id INNER JOIN wms_warehouse w ON inv.warehouse_id = w.id
AND inv.warehouse_id = #{dto.warehouseId}
AND item.item_name LIKE CONCAT('%', #{dto.itemName}, '%')
AND item.item_code LIKE CONCAT('%', #{dto.itemCode}, '%')
AND sku.sku_name LIKE CONCAT('%', #{dto.skuName}, '%')
AND sku.sku_code LIKE CONCAT('%', #{dto.skuCode}, '%')
AND item.item_category = #{dto.itemCategory}
AND item.id = #{dto.itemId}
GROUP BY sku.id, sku.sku_code, sku.sku_name, item.id, item.item_name, item.item_code, w.id, w.warehouse_name
HAVING SUM(inv.quantity) <= #{dto.maxQuantity}
