mapper.sql:
select w.id warehouse_id, w.warehouse_name, item.id item_id, item.item_name, item.item_code, sku.sku_name, sku.id sku_id, sku.sku_code, inventory.quantity, inventory.storage_shelf from wms_inventory inventory inner join wms_item_sku sku on inventory.sku_id = sku.id inner join wms_item item on sku.item_id = item.id inner join wms_warehouse w on inventory.warehouse_id = w.id
item.item_name like concat('%', #{dto.itemName}, '%')
and item.item_code like concat('%', #{dto.itemCode}, '%')
and sku.sku_name like concat('%', #{dto.skuName}, '%')
and sku.sku_code like concat('%', #{dto.skuCode}, '%')
and item.item_category=#{dto.itemCategory}
and item.id=#{dto.itemId}
and inventory.warehouse_id=#{dto.warehouseId}
and inventory.sku_id in ( select sku_id from wms_inventory group by sku_id having sum(quantity) <= #{maxQuantity} )
